import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      fetchTasks(token);
    }
  }, []);

  const fetchTasks = async (token) => {
    const response = await fetch('http://localhost:5000/api/tasks', {
      headers: { Authorization: `Bearer ${token}` }
    });
    const data = await response.json();
    setTasks(data);
  };

  const register = async (name, email, password) => {
    const response = await fetch('http://localhost:5000/api/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, email, password })
    });
    return response.json();
  };

  const login = async (email, password) => {
    const response = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password })
    });
    const data = await response.json();
    if (data.token) {
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      setUser(data.user);
      fetchTasks(data.token);
    }
    return data;
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setTasks([]);
  };

  const addTask = async () => {
    const token = localStorage.getItem('token');
    const response = await fetch('http://localhost:5000/api/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ title: newTask })
    });
    const task = await response.json();
    setTasks([...tasks, task]);
    setNewTask('');
  };

  const toggleTask = async (id) => {
    const token = localStorage.getItem('token');
    const task = tasks.find(t => t._id === id);
    const response = await fetch(`http://localhost:5000/api/tasks/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ status: task.status === 'completed' ? 'pending' : 'completed' })
    });
    const updatedTask = await response.json();
    setTasks(tasks.map(t => t._id === id ? updatedTask : t));
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem('token');
    await fetch(`http://localhost:5000/api/tasks/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` }
    });
    setTasks(tasks.filter(t => t._id !== id));
  };

  if (!user) {
    return (
      <div style={{ padding: '20px', maxWidth: '400px', margin: '0 auto' }}>
        <h2>Login</h2>
        <form onSubmit={async (e) => {
          e.preventDefault();
          await login(e.target.email.value, e.target.password.value);
        }}>
          <input name="email" placeholder="Email" required style={{ margin: '5px', padding: '10px', width: '100%' }} />
          <input name="password" type="password" placeholder="Password" required style={{ margin: '5px', padding: '10px', width: '100%' }} />
          <button type="submit" style={{ margin: '5px', padding: '10px', width: '100%' }}>Login</button>
        </form>

        <h2>Register</h2>
        <form onSubmit={async (e) => {
          e.preventDefault();
          await register(e.target.name.value, e.target.email.value, e.target.password.value);
        }}>
          <input name="name" placeholder="Name" required style={{ margin: '5px', padding: '10px', width: '100%' }} />
          <input name="email" placeholder="Email" required style={{ margin: '5px', padding: '10px', width: '100%' }} />
          <input name="password" type="password" placeholder="Password" required style={{ margin: '5px', padding: '10px', width: '100%' }} />
          <button type="submit" style={{ margin: '5px', padding: '10px', width: '100%' }}>Register</button>
        </form>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>Welcome, {user.name}!</h1>
        <button onClick={logout} style={{ padding: '10px' }}>Logout</button>
      </div>

      <div style={{ margin: '20px 0' }}>
        <input
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add new task..."
          style={{ padding: '10px', width: '70%', marginRight: '10px' }}
        />
        <button onClick={addTask} style={{ padding: '10px' }}>Add Task</button>
      </div>

      <div>
        {tasks.map(task => (
          <div key={task._id} style={{
            padding: '10px',
            margin: '5px 0',
            border: '1px solid #ccc',
            borderRadius: '5px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            backgroundColor: task.status === 'completed' ? '#e8f5e8' : '#fff'
          }}>
            <div>
              <h3 style={{ textDecoration: task.status === 'completed' ? 'line-through' : 'none', margin: 0 }}>
                {task.title}
              </h3>
              <p style={{ margin: 0, color: '#666' }}>{task.description}</p>
            </div>
            <div>
              <button onClick={() => toggleTask(task._id)} style={{ margin: '0 5px', padding: '5px 10px' }}>
                {task.status === 'completed' ? 'Undo' : 'Complete'}
              </button>
              <button onClick={() => deleteTask(task._id)} style={{ margin: '0 5px', padding: '5px 10px', backgroundColor: '#ff4444', color: 'white' }}>
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
